$(function(){"use strict";$(document).on("click",".add-favori",function(a){let t=$(this).attr("article-id");$.ajax({url:"/favoris/new",data:{articleId:t},async:!0,method:"POST",success:function(){$(a.target).removeClass("far").removeClass("add-favori").addClass("fas").addClass("remove-favori")},error:function(a){console.log(a)}})}),$(document).on("click",".remove-favori",function(){let a=$(this).attr("article-id"),t=$(this).attr("data-target");$.ajax({url:"/favoris/"+a+"/delete",method:"DELETE",async:!0,success:function(a){$(t).html(a),$(".bloc-article span.remove-favori").each(function(a,e){$(e).attr("data-target",t)})}})})});